import './App.css';
import { Label, Note } from "./types"; // Import the Label type from the appropriate module
import { dummyNotesList } from "./constants"; // Import the dummyNotesList from the appropriate module
import { useState } from 'react';
import { LikeButtonClicked } from './hooksExercise';

type likeList = {
  setLikeNotes: (notes: string[]) => void;
  likeNotes: string[]; 
};

function App() {
  let [likeNotes, setLikeNotes] = useState([]); 
 return (
   <div className='app-container'>
    <form className="note-form">
       <div><input placeholder="Note Title"></input></div>

       <div><textarea></textarea></div>

       <div><button type="submit">Create Note</button></div>
    </form>
    <div className="notes-grid">
       {dummyNotesList.map((note) => (
         <div
           key={note.id}
           className="note-item">
           <div className="notes-header">
             <LikeButtonClicked setLikeNotes={setLikeNotes} />
             <button>x</button>
           </div>
           <h2> {note.title} </h2>
           <p> {note.content} </p>
           <p> {note.label} </p>
         </div>
       ))}
     </div>
        return (
            <div className="notes-liked">
            <h2>List of Favorites: </h2>
                <ul>
                    {likeList.likeNotes.map((title) => <li>{title}</li>)}
                </ul>
            </div>
        )
    <br/>
   </div>

 );
}

export default App;
