import './App.css';
import { Label, Note } from "./types"; // Import the Label type from the appropriate module
import { dummyNotesList } from "./constants"; // Import the dummyNotesList from the appropriate module
import { useState, useContext } from 'react';
import { LikeButtonClicked, ToggleTheme } from './hooksExercise';
import { ThemeContext, themes } from "./themeContext";

function App() {
  let [likeNotes, setLikeNotes] = useState<Note[]>([]); 
  const theme = useContext(ThemeContext);


const [notes, setNotes] = useState(dummyNotesList); 
const initialNote = {
   id: -1,
   title: "",
   content: "",
   label: Label.other,
 };
const [createNote, setCreateNote] = useState(initialNote);

const createNoteHandler = (event: React.FormEvent) => {
   event.preventDefault();
   console.log("title: ", createNote.title);
   console.log("content: ", createNote.content);
   createNote.id = notes.length + 1;
   setNotes([createNote, ...notes]);
   setCreateNote(initialNote);
 };


 return (
   <div id="head" className='app-container' style={{
      background: theme.background,
      color: theme.foreground,
      padding: "20px",
  }}>

    <ToggleTheme/>
    <form id="form" className="note-form">
       <div><input placeholder="Note Title"></input></div>

       <div><textarea></textarea></div>

       <div><button type="submit">Create Note</button></div>
    </form>
    <div className="notes-grid">
       {dummyNotesList.map((note) => (
         <div
           key={note.id}
           className="note-item">
           <div className="notes-header">
             <LikeButtonClicked setLikeNotes={setLikeNotes} likeNotes={likeNotes} newNote={note} />
             <button>x</button>
           </div>
           <h2> {note.title} </h2>
           <p> {note.content} </p>
           <p> {note.label} </p>
         </div>
       ))}
     </div>
          <div className="notes-item">
          <h2>List of Favorites: </h2>
              <ul>
                  {likeNotes.map((note) => <li key={note.id}>{note.title}</li>)}
              </ul>
          </div>
    <br/>
   </div>

 );
}

export default App;
