import React, { useState, useEffect, useContext, useRef} from 'react';
import { ThemeContext, themes } from "./themeContext";
import './App.css';
import { Label, Note } from "./types"; // Import the Label type from the appropriate module


export function LikeButtonClicked(props: { setLikeNotes: React.Dispatch<React.SetStateAction<Note[]>>; 
                                            likeNotes: Note[]; 
                                            newNote: Note}) {

    const [likeState, setLikeState] = useState("ü§ç");
    const init = useRef(true);
    
    useEffect(() => {
        if (init.current) {
            // Skip running the effect on the first render
            console.log("hi " + props.newNote.title);
            //init.current = false;
        } else {
            console.log("sad" + props.newNote.title);
            if (props.likeNotes.includes(props.newNote)) {
                const newLikeNotes = props.likeNotes.filter(aNote => aNote !== props.newNote);
                props.setLikeNotes([...newLikeNotes]);
            }
            else {
                props.setLikeNotes([...props.likeNotes, props.newNote]);
            }
        }
    }, [likeState]);

    const toggleLike = () => {
        setLikeState(likeState === "ü§ç" ? "‚ù§Ô∏è" : "ü§ç");
    }

    return (
        <button onClick={toggleLike}>{likeState}</button>
    );
}

export function ToggleTheme() {
    const [currentTheme, setCurrentTheme] = useState(themes.light);
   
    const toggleTheme = () => {
        setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

    useEffect(() => {
        const app = document.getElementById("head");
        if (app != null) {
            app.style.backgroundColor = currentTheme.foreground;
            app.style.color = currentTheme.background;
        }

        const form = document.getElementById("form");
        if (form != null) {
            form.style.backgroundColor = currentTheme.foreground;
            form.style.color = currentTheme.background;
        }

        const noteList = document.getElementsByClassName("note-item");
        for (let i = 0; i < noteList.length; i++) {
            const aNote = noteList[i];
            if (aNote instanceof HTMLElement) {
                aNote.style.backgroundColor = currentTheme.foreground;
                aNote.style.color = currentTheme.background;
            }
        }
    }, [toggleTheme]);
   
    return (
      <ThemeContext.Provider value={currentTheme}>
        <button onClick={toggleTheme}> Toggle Theme </button>
        <br/>
      </ThemeContext.Provider>
    );
}
   


function ClickCounter() {
 const [count, setCount] = useState(0);
 const theme = useContext(ThemeContext);

 const handleClick = () => {
   setCount(count + 1);
 };

 useEffect(() => {
   document.title = `You clicked ${count} times`;
 }, [count]);

 return (
    <div
    style={{
      background: theme.background,
      color: theme.foreground,
      padding: "20px",
    }}
  >
    <p>You clicked {count} times </p>
    <button
      onClick={() => setCount(count + 1)}
      style={{ background: theme.foreground, color: theme.background }}
    >
      Click me
    </button>
  </div>

 );
}
